import React from "react";
import { connect } from "react-redux";
import Product from "../ components/Product";
import Category from "../ components/Category";

function getCategory(product, categories) {
  return categories.find(category => category.id === product.categoryId);
}
class ProductsPage extends React.Component {
  componentDidMount() {
    this.loadData(this.props);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.match.url !== this.props.match.url) {
      this.loadData(nextProps);
    }
  }

  loadData(props) {
    if (props.match.path === "/categories/:id") {
      var productsQueryParams = {
        filter: { categoryId: props.match.params.id }
      };
    }

    props.loadAll(productsQueryParams);
    props.loadCategories();
  }

  render() {
    return (
      <div>
        <h1>Products Page {this.props.products.length}</h1>

        <div>
          {this.props.categories.map(category => (
            <Category id={category.id} category={category} />
          ))}
        </div>
        <div>
          {this.props.products.map(product => (
            <Product
              id={product.id}
              product={product}
              category={getCategory(product, this.props.categories)}
            />
          ))}
        </div>
      </div>
    );
  }
}

const mapState = state => ({
  products: state.products.list,
  categories: state.categories.list
});

const mapDispatch = ({ products, categories }) => ({
  loadAll: products.loadAll,
  loadCategories: categories.loadCategories
});

export default connect(
  mapState,
  mapDispatch
)(ProductsPage);
