import { loadCategories } from "../libs/api";

function createModel(loadCategories) {
  return {
    state: {
      list: [],
      loadingStatus: "noLoaded"
    },
    reducer: {
      setList(state, list) {
        state.list = list;
      }
    }
  };
}
