const cart = {
  state: {
    products: [
      {
        productId: "1",
        quantity: 1
      }
    ]
  },
  reducers: {
    addProduct(state, productId) {
      let product = {
        productId: productId,
        quantity: 1
      };
      return state.products.push(product);
    },
    removeProduct(state, productId) {
      let productCurent = state.products.find(
        product => product.productId === productId
      );
      let productsNew = state.products.filter(
        product => product.productId !== productCurent.productId
      );
      return (state.products = productsNew);
    },
    setQuantity(state, productId, qty) {
      const productCurent = state.products.find(
        product => product.productId === productId
      );
      const indexProduct = state.products.indexOf(productCurent);
      console.log(productCurent.productId, indexProduct);
      console.log(...state);
      return {
        ...state,
        products: [
          ...state.products.slice(0, indexProduct),
          {
            ...productCurent,
            quantity: qty
          },
          ...state.products.slice(indexProduct + 1)
        ]
      };
    }
  }
};
export default cart;
