const baseUrl = "https://rest-api-broot.glitch.me";

function checkStatus(response) {
  if (response.ok) {
    return response;
  } else {
    throw new Error("server error");
  }
}

function readJson(response) {
  return response.json();
}

export function loadProducts() {
  return fetch(`${baseUrl}/api/mariia/products`)
    .then(r => r.json())
    .then(checkStatus)
    .then(readJson);
}
export function loadProduct(id) {
  return fetch(`${baseUrl}/api/mariia/products/${id}`)
    .then(r => r.json())
    .then(checkStatus)
    .then(readJson);
}
