import React from "react";
import { connect } from "react-redux";
import Product from "../ components/Product";
import Category from "../ components/Category";
import categoryForProductSelector from "../selectors/getSelectorsCategory";
import CartButton from "../ components/CartButton";

class ProductsPage extends React.Component {
  componentDidMount() {
    this.loadData(this.props);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.match.url !== this.props.match.url) {
      this.loadData(nextProps);
    }
  }

  loadData(props) {
    if (props.match.path === "/categories/:id") {
      var productsQueryParams = {
        filter: { categoryId: props.match.params.id }
      };
    }

    props.loadAll(productsQueryParams);
    props.loadCategories();
  }

  render() {
    return (
      <div>
        <div>
          <CartButton />
        </div>
        <div>
          <p>Categories Page {this.props.categories.length}</p>
          {this.props.categories.map(category => (
            <Category key={category.id} id={category.id} category={category} />
          ))}
        </div>
        <div>
          <p>Products Page {this.props.products.length}</p>
          {this.props.products.map(product => (
            <Product
              key={product.id}
              id={product.id}
              product={product}
              addProduct={this.props.addProduct}
              removeProduct={this.props.removeProduct}
              setQuantity={this.props.setQuantity}
              category={categoryForProductSelector(
                product,
                this.props.categories
              )}
            />
          ))}
        </div>
      </div>
    );
  }
}

const mapState = state => ({
  products: state.products.list,
  categories: state.categories.list
});

const mapDispatch = ({ products, categories, productsInCart }) => ({
  loadAll: products.loadAll,
  loadCategories: categories.loadCategories,
  addProduct: productsInCart.addProduct
});

export default connect(
  mapState,
  mapDispatch
)(ProductsPage);
