import React from "react";
import { connect } from "react-redux";
import CartItem from "../ components/CartItem";
class CartPage extends React.Component {
  render() {
    return (
      <div>
        <h1>Cart</h1>
        {this.props.cartItems.map(item => (
          <CartItem
            key={item.product.id}
            product={item.product}
            quantity={item.quantity}
            setQuantity={this.props.setQuantity}
            removeProduct={this.props.removeProduct}
          />
        ))}
      </div>
    );
  }
}

const mapState = state => ({
  cartItems: state.productsInCart.items
});

const mapDispatch = ({ productsInCart }) => ({
  removeProduct: productsInCart.removeProduct,
  setQuantity: productsInCart.setQuantity,

  addOne: () => dispatch.productsInCart.addProduct(ownProps.product.id),
  removeOne: () => dispatch.productsInCart.addProduct(ownProps.product.id)
});

export default connect(
  mapState,
  mapDispatch
)(CartPage);
