import React from "react";
import { connect } from "react-redux";

function getProductById(products, id) {
  return products.find(p => p.id === id);
}

function CartItem({ product, quantity }) {
  return (
    <div>
      {product.name}-{quantity}
    </div>
  );
}
class CartPage extends React.Component {
  componentDidMount() {
    this.props.loadAll();
  }
  render() {
    return (
      <div>
        <h1>Cart</h1>
        {this.props.cartItems.map(item => (
          <CartItem
            key={item.productId}
            product={getProductById(this.props.products, item.productId)}
            quentity={item.quantity}
          />
        ))}
        {console.log(this.props.cartItems)}
      </div>
    );
  }
}

const mapState = state => ({
  products: state.products.list,
  cartItems: state.productsInCart.products
});

const mapDispatch = ({ products, categories, productsInCart }) => ({
  loadAll: products.loadAll
  // loadCategories: categories.loadCategories,
  // addProduct: productsInCart.addProduct,
  // removeProduct: productsInCart.removeProduct,
  // setQuantity: productsInCart.setQuantity
});

export default connect(
  mapState,
  mapDispatch
)(CartPage);
