import { loadProducts } from "../libs/api";
function createModel(loadProducts) {
  return {
    state: {
      list: []
    },
    reducers: {
      setList(state, list) {
        state.list = list;
      }
    },
    effects: dispatch => ({
      loadAll(payload, rootState) {
        loadProducts().then(data => this.setList(data));
      }
    })
  };
}

export { createModel };
export default createModel(loadProducts);
