const baseUrl = "https://rest-api-broot.glitch.me";

export function loadProducts() {
  return fetch(`${baseUrl}/api/mariia/products`).then(r => r.json());
}
export function loadProduct(id) {
  return fetch(`${baseUrl}/api/mariia/products/${id}`).then(r => r.json());
}
