export default {
  state:{
    list: []
  },
  reducers:{
    setList(state, list){
      state.list = list
    }
  },
  effects: dispatch =>({
      loadAll(payload, rootState){
        fetch('/data/products.json')
        .then(r=>r.json())
        .then(data=>this.setList(data))
      }
    })
}
