const count = {
  state: 0,
  reducers: {
    addBy(state, payload) {
      return state + payload;
    }
  }
};

const cart = {
  state: {
    products: [
      {
        productId: "1",
        quantity: 1
      }
    ]
  },
  reducers: {
    addProduct(state, productId) {
      let product = {
        productId: productId,
        quantity: 1
      };
      return state.products.push(product);
    },
    removeProduct(productId) {},
    setQuantity(productId, quantity) {}
  }
};
export default cart;
