import React from 'react'
import { connect } from "react-redux";
import Product from '../ components/Product'

class ProductsPage extends React.Component{
  componentDidMount(){
    this.props.loadAll()
  }

  render(){
    return (
      <div>
        <h1>Products Page {this.props.products.length}</h1>
        <div>{this.props.products.map(product=> {
                return (<div><Product product={product}/></div>)
                }
              )}
        </div>
      </div>

    )
  }
}

const mapState = state => ({
  products: state.products.list,
});

const mapDispatch = ({ products: { loadAll}}) => ({
  loadAll
});

export default connect(mapState, mapDispatch)(ProductsPage);
