import React from "react";
import { connect } from "react-redux";
export function CartButton({ productsQuantity, totalPrice }) {
  return (
    <div>
      Cart {productsQuantity} products, price: {totalPrice}
    </div>
  );
}

function numberProductsInCart(cart) {
  return cart.products.reduce((prev, product) => prev + product.quantity, 0);
}
const mapState = state => ({
  productsQuantity: state.numberProductsInCart(productsInCart),
  totalPrice: 0
});

export default connect(mapState)(CartButton);
