import React from "react";

export default function CartItem({ product, quantity, setQuantity }) {
  return (
    <div>
      {product.name}-{quantity}
      <input
        type="number"
        onChange={e => setQuantity(product.id, e.target.value)}
      />
    </div>
  );
}
