const cart = {
  state: {
    productsInCart: [
      {
        productId: "1",
        quantity: 1
      }
    ]
  },
  reducers: {
    addProduct(productId) {},
    removeProduct(productId) {},
    setQuantity(productId, quantity) {}
  }
};
