import React from "react";
import { connect } from "react-redux";
import CategoryExtended from "../ components/CategoryExtended";

class CategoryPage extends React.Component {
  componentDidMount() {
    this.props.loadCategory(this.props.match.params.id);
  }

  render() {
    return (
      <div>
        {this.props.category ? (
          <CategoryExtended category={this.props.category} />
        ) : (
          "loading"
        )}
      </div>
    );
  }
}

const mapState = (state, props) => ({
  product: state.products.list.find(
    product => product.id === props.match.params.id
  )
});

const mapDispatch = ({ category: { loadCategory } }) => ({
  loadCategory
});

export default connect(
  mapState,
  mapDispatch
)(CategoryPage);
