const cart = {
  state: {
    products: [
      {
        productId: "1",
        quantity: 1
      }
    ]
  },
  reducers: {
    addProduct(state, productId) {
      let product = {
        productId: productId,
        quantity: 1
      };
      return state.products.push(product);
    },
    removeProduct(productId) {},
    setQuantity(productId, quantity) {}
  }
};
export default cart;
