import React from "react";
import { connect } from "react-redux";

import getProductById from "../selectors/getProductById";
import CartItem from "../ components/CartItem";
class CartPage extends React.Component {
  componentDidMount() {
    this.props.loadAll();
  }
  render() {
    return (
      <div>
        <h1>Cart</h1>
        {this.props.cartItems.map(item => (
          <CartItem
            key={item.product.id}
            product={getProductById(this.props.products, item.productId)}
            quantity={item.quantity}
            setQuantity={this.props.setQuantity}
            removeProduct={this.props.removeProduct}
          />
        ))}
      </div>
    );
  }
}

const mapState = state => ({
  products: state.products.list,
  cartItems: state.productsInCart.items
});

const mapDispatch = ({ products, productsInCart }) => ({
  loadAll: products.loadAll,
  removeProduct: productsInCart.removeProduct,
  setQuantity: productsInCart.setQuantity
});

export default connect(
  mapState,
  mapDispatch
)(CartPage);
